mapper.select:
SELECT a.order_id as orderId,a.order_code as orderCode,a.create_order_person as createOrderPerson,b.id, b.work_date as workDate,b.style_code as styleCode,b.style_name as styleName,b.ascription,b.valuation_type as valuationType, b.persons,b.count,b.price,b.increase_price as increasePrice,b.total_price as totalPrice, b.check_person as checkPerson,b.check,b.remark FROM orders as a ,order_style AS b WHERE a.order_id = b.order_id AND a.order_id IN (
#{orderId}
)

mapper.update:
UPDATE order_style

    "check" = 1,
    check_person = #{checkPerson}

WHERE id IN

    #{orderStyleId}
